changeSet.sql:
CREATE TABLE `process_node` (
id serial PRIMARY KEY,
`name` VARCHAR(255),
`description` VARCHAR(255),
`code` VARCHAR (50) NOT NULL
);

CREATE TABLE `process_cluster` (
id serial PRIMARY KEY,
`name` VARCHAR(255),
`description` VARCHAR(255),
`code` VARCHAR (50) NOT NULL
);

CREATE TABLE `cluster_node_package` (
id serial PRIMARY KEY,
`cluster-node-id` INT,
`package-id` INT,
`add-time` DATE
);
